    def test_v4dsa_s2k3(self):
        "packet.PrivateKey: v4 DSA w/ S2K type 3"
        s2k_values = {'spec': 3,
                      'k_hash': 2,
                      'salt': "\x17\xc6\xb2\x3d\x31\xa0\xe1\x63",
                      'count_code': 96,
                      # derived
                      'count': 65536}
        s2k = S2K()
