'''Settings for configuring RIP.'''

# Report only errors, not warnings. DocUtils default is 2.
REPORT_LEVEL = 3

# Start headers off at level 3. DocUtils default is 1; possible values are 1-6.
INITIAL_HEADER_LEVEL = 3

# Don't remove the top-most header in the document with an aim to publish it as
# the document title. DocUtils default is True.
REMOVE_INITIAL_HEADER = False

# Format for footnote references, either 'superscript' or 'brackets'. DocUtils
# default is 'brackets'.
FOOTNOTE_REFERENCES = 'superscript'

# Number of spaces to expand a tab to.
TAB_WIDTH = 4
