function LinkAutoScroll(){this.items=[];this.addItem=function(adURL,rel,fitClass,strURL,strBak,intTarget){var newItem={};newItem.adURL=adURL;newItem.rel=rel;newItem.fitClass=fitClass;newItem.strURL=strURL;newItem.strBak=strBak;newItem.intTarget=intTarget;this.items[this.items.length]=newItem;};this.play=function(){LinkCollectScroll=this.items;LinkScrollHtml();Ltab=document.getElementById(rootdiv);Ltab1=document.getElementById(son1);Ltab2=document.getElementById(son2);Ltab2.innerHTML=Ltab1.innerHTML;MyL=setInterval(MyMarqueeLink,Lspeed);};};function LinkScrollHtml(){var imgHtml="";for(var i=0;i<LinkCollectScroll.length;i++){var a=LinkCollectScroll[i];imgHtml+="<a href=\""+a.strURL+"\" rel=\""+a.rel+"\" class=\""+a.fitClass+"\"  target=\""+a.intTarget+"\"><img src=\""+a.adURL+"\" alt=\""+a.strBak+"\" border=\"0\" /></a>";};var b,c,d;if(LinkDirection=="left"||LinkDirection=="right"){b="<div id=\"indemo\" style=\"float:left;width:800%\">";c=" style=\"float:left\"";d="</div>";}else{b="";c="";d="";};document.writeln("<div align=\"center\" >");document.writeln("<div id="+rootdiv+" onmouseover=\"clearInterval(MyL)\" onmouseout=\"MyL=setInterval(MyMarqueeLink,Lspeed)\" style=\"overflow:hidden;width:"+LAWidth+"px;height:"+LAHeight+"px\">");document.writeln(b+"<div id="+son1+" "+c+">");document.writeln(imgHtml);document.writeln("</div><div id="+son2+" "+c+">"+d+"</div></div></div>");};function MyMarqueeLink(){if(LinkDirection=="top"){if(Ltab2.offsetTop-Ltab.scrollTop<=0){Ltab.scrollTop-=Ltab1.offsetHeight;}else{Ltab.scrollTop++;}}else if(LinkDirection=="down"){if(Ltab1.offsetTop-Ltab.scrollTop>=0){Ltab.scrollTop+=Ltab2.offsetHeight;}else{Ltab.scrollTop--;}}else if(LinkDirection=="left"){if(Ltab2.offsetWidth-Ltab.scrollLeft<=0){Ltab.scrollLeft-=Ltab1.offsetWidth;}else{Ltab.scrollLeft++;}}else if(LinkDirection=="right"){if(Ltab.scrollLeft<=0){Ltab.scrollLeft+=Ltab2.offsetWidth;}else{Ltab.scrollLeft--;}}};


